const rawOutput = $input.first().json.output;

// Limpiar prefijo "json\n" si estÃ¡ presente
const cleanedOutput = rawOutput.trim().replace(/^json\s*/, '');

// Primer parseo
let firstParsed;
try {
  firstParsed = JSON.parse(cleanedOutput);
} catch (e) {
  throw new Error("No se pudo hacer el primer parseo del output. Contenido: " + rawOutput);
}

// Extraer campos
const mensaje = firstParsed.respuesta || '';
const tool = firstParsed.tool || null;
const urlFromField = firstParsed.url || null;

// Extraer todas las URLs desde el mensaje (texto)
const urlRegex = /(https?:\/\/[^\s\)\]]+)/g;
const urlsInText = mensaje.match(urlRegex) || [];

// Incluir la del campo si no estÃ¡ repetida
if (urlFromField && !urlsInText.includes(urlFromField)) {
  urlsInText.push(urlFromField);
}

// Eliminar URLs del texto
let textoSinUrl = mensaje;
urlsInText.forEach(url => {
  textoSinUrl = textoSinUrl.replace(url, '').trim();
});

// Resultado final
return [
  {
    json: {
      texto: textoSinUrl,
      urls: urlsInText,  // lista de todas las URLs
      tool: tool
    }
  }
];
